mapper.select:
        select playerid, actorid, rank, level
          from server_player_actor


				and playerid = #{playerid, jdbcType=VARCHAR}


				and actorid = #{actorid, jdbcType=VARCHAR}


				and rank = #{rank, jdbcType=INTEGER}


				and level = #{level, jdbcType=INTEGER}

select playerid, actorid, rank, level
  from server_player_actor
 where 1=1 and playerid = #{arg0} and actorid = #{arg1}

mapper.update:
update server_player_actor set 
       rank = #{rank, jdbcType=INTEGER}, 
       level = #{level, jdbcType=INTEGER}
 where 1=1 and playerid = #{playerid, jdbcType=VARCHAR} and actorid = #{actorid, jdbcType=VARCHAR}
