mapper.select:
        select 
          from server_player_info


				and playerid = #{playerid, jdbcType=VARCHAR}


				and serverid = #{serverid, jdbcType=VARCHAR}


				and custno = #{custno, jdbcType=INTEGER}


				and playernick = #{playernick, jdbcType=VARCHAR}


				and logindate = #{logindate, jdbcType=VARCHAR}


				and logintime = #{logintime, jdbcType=VARCHAR}


				and level = #{level, jdbcType=INTEGER}

select 
  from server_player_info
 where 1=1 and playerid = #{0}
