mapper.select:
        select 
          from server_player_item


				and playid = #{playid, jdbcType=VARCHAR}


				and itemid = #{itemid, jdbcType=VARCHAR}


				and num = #{num, jdbcType=INTEGER}

select 
  from server_player_item
 where 1=1 and playid = #{0} and itemid = #{1}

mapper.update:
update server_player_item set 
       num = #{num, jdbcType=INTEGER}
 where 1=1 and playid = #{playid, jdbcType=VARCHAR} and itemid = #{itemid, jdbcType=VARCHAR}

update server_player_item


		num = #{num, jdbcType=INTEGER},


where 1=1 and playid = #{playid, jdbcType=VARCHAR} and itemid = #{itemid, jdbcType=VARCHAR}
