mapper.select:
        select custno, msgid, isread
          from sys_cust_message


				and custno = #{custno, jdbcType=INTEGER}


				and msgid = #{msgid, jdbcType=INTEGER}


				and isread = #{isread, jdbcType=VARCHAR}

select custno, msgid, isread
  from sys_cust_message
 where 1=1 and custno = #{arg0} and msgid = #{arg1}

mapper.update:
update sys_cust_message set 
       isread = #{isread, jdbcType=VARCHAR}
 where 1=1 and custno = #{custno, jdbcType=INTEGER} and msgid = #{msgid, jdbcType=INTEGER}
