mapper.select:
        select msgid, content, author, senddate, sendtime
          from sys_message


				and msgid = #{msgid, jdbcType=INTEGER}


				and content = #{content, jdbcType=VARCHAR}


				and author = #{author, jdbcType=INTEGER}


				and senddate = #{senddate, jdbcType=VARCHAR}


				and sendtime = #{sendtime, jdbcType=VARCHAR}

select msgid, content, author, senddate, sendtime
  from sys_message
 where 1=1 and msgid = #{arg0}

mapper.update:
update sys_message set 
       content = #{content, jdbcType=VARCHAR}, 
       author = #{author, jdbcType=INTEGER}, 
       senddate = #{senddate, jdbcType=VARCHAR}, 
       sendtime = #{sendtime, jdbcType=VARCHAR}
 where 1=1 and msgid = #{msgid, jdbcType=INTEGER}
